import React, { useState, useRef } from 'react';
import classes from './Auth.module.css';

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  // we want to output our Values In the Input Fields...
  const emailRef = useRef();
  const passwordRef = useRef();

  // To toggle between (LOGIN && Sign UP)
  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const submitHandeler = (e) => {
    e.preventDefault();

    setIsLoading(true);
    // Handling The Fetching Progress
    const enteredEmail = emailRef.current.value;
    const enteredPassword = passwordRef.current.value;
    const url =
      'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBcKcoWoh0SZEY8r9GsL9x-lYW7PRNAuo8';

    fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        email: enteredEmail,
        password: enteredPassword,
        returnSecureToken: true,
      }),
    }).then((res) => {
      if (res.ok) {
        return res.json().then((data) => console.log(data));
      } else {
        return res
          .json()
          .then((data) => {
            let errorMessage = data.error.message;
            throw new Error(errorMessage);
          })
          .catch((error) => {
            alert(error.message);
          });
      }
    });
  };

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandeler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input ref={emailRef} type='email' id='email' required />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input ref={passwordRef} type='password' id='password' required />
        </div>
        <div className={classes.actions}>
          {!isLoading && (
            <button>{isLogin ? 'Login' : 'Create Account'}</button>
          )}
          {isLoading && <p>Sending Request....</p>}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Auth;

