import React, { useState } from 'react';
import classes from './TaskForm.module.css';
import TaskList from './TaskList';

const Taskform = () => {
  // Responsble On Posting THE data Inside The Form. (( Post Request ))
  const [enteredDetails, setEnteredDetails] = useState('');
  const [enteredEndTime, setEnteredEndTime] = useState('');
  const [isEmpty, setIsEmpty] = useState(true);
  //   const TaskDetailsRef = useRef();
  //   const TaskEndTimeRef = useRef();
  const TaskEndTimeValueHandeler = (e) => {
    setEnteredEndTime(e.target.value);
  };
  const TaskDetailsValueHandeler = (e) => {
    setEnteredDetails(e.target.value);
  };

  const Input = [
    {
      taskDetails: enteredDetails,
      taskDate: enteredEndTime,
      id: Math.random(10),
    },
  ];
  const submitHandeler = (e) => {
    e.preventDefault();

    // const EnteredDetails = TaskDetailsRef.current.value;
    // const EnteredEndTime = TaskEndTimeRef.current.value;
    console.log(enteredDetails);
    console.log(enteredEndTime);
    setIsEmpty(false);
  };
  return (
    <>
      <section className={classes.auth}>
        <h1>Enter Your Tasks</h1>

        <form onSubmit={submitHandeler}>
          <div className={classes.control}>
            <label>Task Details</label>
            <textarea
              onChange={TaskDetailsValueHandeler}
              vlaue={enteredDetails}
            />
          </div>
          <div className={classes.control}>
            <label>Task End Time</label>
            <input onChange={TaskEndTimeValueHandeler} value={enteredEndTime} />
          </div>
          <div className={classes.actions}>
            <button onSubmit={submitHandeler}>Submit Your Task</button>
          </div>
        </form>
      </section>
      {isEmpty && <p>No Tasks Here</p>}
      {!isEmpty && <TaskList Inputs={Input} />}
    </>
  );
};

export default Taskform;
