import React, { useState } from 'react';
import classes from './TaskList.module.css';

const TaskList = (props) => {
  const [tasksDetails, setTasksDetails] = useState([]);
  // Responsable On Get The Data In The Form (( Get Request))

  const FetchData = async () => {
    const response = await fetch(
      'https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json'
    );
    const data = await response.json();

    const gettedData = [];
    for (const key in data) {
      gettedData.push({
        Title: data[key].taskDetails,
        date: data[key].taskEndDate,
        id: data[key].taskId,
      });
    }
    setTasksDetails(gettedData);
  };

  const id = tasksDetails.id;

  const deleteHandeler = () => {
    tasksDetails.map((task) => console.log(task.id));
  };
  return (
    <>
      <section className={classes.container}>
        <button className={classes.btn} onClick={FetchData}>
          Get My Data
        </button>

        {tasksDetails.map((task) => (
          <div className={classes.task}>
            <h3>{task.Title}</h3>
            <h3>{task.date}</h3>
            <h3>{task.id}</h3>
            <button className={classes.btn} onClick={deleteHandeler}>
              Delete
            </button>
          </div>
        ))}
      </section>
    </>
  );
};

export default TaskList;
