import React, { useState } from 'react';
import classes from './TaskList.module.css';

const TaskList = () => {
  const [tasksDetails, setTasksDetails] = useState([]);
  // Responsable On Get The Data In The Form (( Get Request))

  const FetchData = async () => {
    const response = await fetch(
      'https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json'
    );
    const data = await response.json();

    let gettedData = [];

    for (const key in data) {
      gettedData.push({
        Title: data[key].taskDetails,
        date: data[key].taskEndDate,
        id: data[key].taskId,
      });
    }
    setTasksDetails(gettedData);
  };

  const deleteHandeler = () => {
    // for (let i = 0; i >= tasksDetails.length; i++) {
    //   console.log(tasksDetails[i].id);
    // }
    tasksDetails.forEach((task) => {
      console.log(task.id);
    });
  };
  return (
    <>
      <section className={classes.container}>
        <button className={classes.btn} onClick={FetchData}>
          Get My Data
        </button>

        {tasksDetails.map((task) => (
          <>
            <div key={task.id} className={classes.task}>
              <h3>{task.Title}</h3>
              <p>{task.date}</p>
            </div>
            <div>
              <button onClick={deleteHandeler} className={classes.btn}>
                Delete
              </button>
            </div>
          </>
        ))}
      </section>
    </>
  );
};

export default TaskList;
