import React, { useState } from 'react';
import classes from './TaskList.module.css';
import TaskForm from './TaskForm';
import TaskList from './TaskList';

const Task = () => {
  const [taskDetails, setTaskDetails] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // MAKING OUR REALTIME DATA BASE URL CONSTANT

  const url =
    'https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json';

  // POSTING REQUEST:

  const posting = async (TasksInputs) => {
    const response = await fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(TasksInputs),
    });

    const data = await response.json();
    console.log(data);
  };

  // GET REQUEST:

  const getData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Somthing Went Wrong');
      }
      const data = await response.json();

      const gettedData = [];

      for (const key in data) {
        gettedData.push({
          title: data[key].taskDetails,
          date: data[key].taskEndDate,
          id: data[key].taskId,
        });
      }
      setTaskDetails(gettedData);
    } catch (error) {
      setError(error);
    }
    setIsLoading(false);
  };

  // HANDLING THE DELETE METHOD (( NOT FROM THE DATA BASE !!))

  const deleteHandeler = (id) => {
    const FilteredTasks = taskDetails.filter((task) => task.id !== id);
    setTaskDetails(FilteredTasks);
  };
  // Handling Errors And Loading Status
  let content;

  if (taskDetails.length > 0) {
    content = <TaskList onDeleteMe={deleteHandeler} taskGetted={taskDetails} />;
  }
  if (taskDetails.length === 0) {
    content = <p className={classes.section}>Found No Tasks</p>;
  }

  if (error) {
    content = <p className={classes.section}>Some Thing Went Wrong</p>;
  }
  if (isLoading) {
    content = <p className={classes.section}>Loading...</p>;
  }

  return (
    <>
      <section>
        <TaskForm onAddTask={posting} />
      </section>
      <section className={classes.section}>
        <button className={classes.btn} onClick={getData}>
          Get My Data
        </button>
      </section>
      <section>{content}</section>
    </>
  );
};

export default Task;
