import React, { useState } from 'react';
import classes from './TaskList.module.css';

const TaskList = (props) => {
  const [tasksDetails, setTasksDetails] = useState([]);

  // Responsable On Get The Data In The Form (( Get Request))

  const getData = async () => {
    const response = await fetch(
      'https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json'
    );
    const data = await response.json();

    let gettedData = [];

    for (const key in data) {
      gettedData.push({
        Title: data[key].taskDetails,
        date: data[key].taskEndDate,
        id: data[key].taskId,
      });
    }
    setTasksDetails(gettedData);
  };
  const deletHandeler = () => {
    fetch(
      `https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json/${tasksDetails.map(
        (c) => c.id
      )}`,
      {
        method: 'DELETE',
      }
    )
      .then((res) => res.json())
      .then((data) => console.log(data));
  };
  return (
    <>
      <section className={classes.container}>
        <button className={classes.btn} onClick={getData}>
          Get My Data
        </button>
        {tasksDetails.map((task) => (
          <div key={task.id} className={classes.task}>
            <div className={classes.flex}>
              <h3>{task.Title}</h3>
              <p>{task.date}</p>
            </div>
            <button className={classes.btn} onClick={deletHandeler}>
              Delete
            </button>
            {/* <button
              className={classes.btn}
              onClick={() => {
                props.onGetData(task.id);
              }}
            >
              Delete
            </button> */}
          </div>
        ))}
      </section>
    </>
  );
};

export default TaskList;
