import React, { useRef } from 'react';
import classes from './TaskForm.module.css';
import TaskList from './TaskList';

const Taskform = () => {
  // Responsble On Posting THE data Inside The Form. (( Post Request ))

  const TaskDetailsRef = useRef();
  const TaskEndTimeRef = useRef();
  //   const [] = useState([]);

  const submitHandeler = (e) => {
    e.preventDefault();

    const randomNum = Math.round(Math.random() * 100);
    const EnteredDetails = TaskDetailsRef.current.value;
    const EnteredEndTime = TaskEndTimeRef.current.value;
    // POSTING REQUEST:

    const url =
      'https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json';

    fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        taskDetails: EnteredDetails,
        taskEndDate: EnteredEndTime,
        taskId: randomNum,
      }),
    }).then((res) => {
      if (res.ok) {
        return res.json().then((data) => console.log(data));
      } else {
        return res
          .json()
          .then((data) => {
            let errorMessage = data.error.message;
            throw new Error(errorMessage);
          })
          .catch((error) => {
            alert(error.message);
          });
      }
    });

    // GET REQUEST
    // const get = () => {
    //   const response = fetch(
    //     'https://second-demo-auth-post-get-default-rtdb.firebaseio.com/Task.json'
    //   );
    //   const gettedData = response.json();

    //   const loadedData = [];
    //   for (const key in gettedData) {
    //     loadedData.push({
    //       id: key,
    //       taskTitle: gettedData[key].taskTitle,
    //       taskEndDate: gettedData[key].taskEndDate,
    //     });
    //   }
    //   setTaskData(loadedData);
    // };
    // get();
  };

  return (
    <>
      <section className={classes.auth}>
        <h1>Enter Your Tasks</h1>

        <form onSubmit={submitHandeler}>
          <div className={classes.control}>
            <label>Task Details</label>
            <textarea ref={TaskDetailsRef} />
          </div>
          <div className={classes.control}>
            <label>Task End Time</label>
            <input ref={TaskEndTimeRef} />
          </div>
          <div className={classes.actions}>
            <button onSubmit={submitHandeler}>Submit Your Task</button>
          </div>
        </form>
      </section>
      <TaskList />
    </>
  );
};

export default Taskform;
