      }
      const data = await response.json();

      const gettedData = [];

      for (const key in data) {
        gettedData.push({
          title: data[key].taskDetails,
          date: data[key].taskEndDate,
          id: data[key].taskId,
        });
      }
      setTaskDetails(gettedData);
    } catch (error) {
      setError(error.massage);
    }
    setIsLoading(false);
  };

